import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Grid, Card, CardContent, Typography, Avatar, Box, Button } from '@mui/material';
import axios from 'axios';

function Profile() {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userResponse = await axios.get(`http://localhost:8080/api/users/${id}`);
        setUser(userResponse.data);
        
        const postsResponse = await axios.get(`http://localhost:8080/api/posts`);
        const userPosts = postsResponse.data.filter(post => post.author?.id === parseInt(id));
        setPosts(userPosts);
      } catch (error) {
        console.error('Erro ao buscar dados do usuário:', error);
      }
    };

    fetchUserData();
  }, [id]);

  if (!user) {
    return <Typography>Carregando...</Typography>;
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box display="flex" alignItems="center" gap={3}>
            <Avatar
              src={user.profilePicture}
              sx={{ width: 100, height: 100 }}
            >
              {user.username.charAt(0)}
            </Avatar>
            <Box>
              <Typography variant="h4">{user.fullName}</Typography>
              <Typography variant="subtitle1" color="text.secondary">
                @{user.username}
              </Typography>
              <Typography variant="body1" sx={{ mt: 1 }}>
                {user.bio}
              </Typography>
              <Button variant="contained" sx={{ mt: 2 }}>
                Seguir
              </Button>
            </Box>
          </Box>
        </CardContent>
      </Card>

      <Typography variant="h5" sx={{ mb: 2 }}>
        Posts
      </Typography>
      <Grid container spacing={3}>
        {posts.map((post) => (
          <Grid item xs={12} key={post.id}>
            <Card>
              <CardContent>
                <Typography variant="body1" paragraph>
                  {post.content}
                </Typography>
                {post.imageUrl && (
                  <img
                    src={post.imageUrl}
                    alt="Post content"
                    style={{ maxWidth: '100%', height: 'auto' }}
                  />
                )}
                <Typography variant="caption" color="text.secondary">
                  {new Date(post.createdAt).toLocaleDateString()}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default Profile;
